  def self.auto_set
    etho = `cat     /sys/class/net/eth0/address`
    etho.gsub!("\n", '')
    pp etho
    ethos = etho.split(":")
    1.upto 6 do |k|
      if k == 1
        etho = "#{ethos[0]}:#{ethos[1]}:#{ethos[2]}:#{ethos[3]}:#{ethos[4]}:#{ethos[5]}"
